import React, { useEffect, useState } from "react";
import ProductCard from "../components/ProductCard";

function Home() {
  const [products, setProducts] = useState([]);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    fetch("/products")
      .then((res) => res.json())
      .then((data) => setProducts(data))
      .catch((err) => console.error("Error cargando productos:", err));
  }, []);

  const displayed = products.filter((p) => {
    if (filter === "all") return true;
    if (filter === "gorras") return p.name.toLowerCase().includes("gorra");
    return true;
  });

  const buttonStyle = {
    padding: "8px 12px",
    marginRight: "8px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    background: "white",
    cursor: "pointer",
  };
  const activeStyle = {
    background: "#222",
    color: "white",
    borderColor: "#222",
  };

  return (
    <div>
      <h1>Productos</h1>

      <div style={{ marginBottom: 12 }}>
        <button
          onClick={() => setFilter("all")}
          style={{ ...buttonStyle, ...(filter === "all" ? activeStyle : {}) }}
        >
          Todos
        </button>
        <button
          onClick={() => setFilter("gorras")}
          style={{ ...buttonStyle, ...(filter === "gorras" ? activeStyle : {}) }}
        >
          Gorras
        </button>
      </div>

      {displayed.length === 0 ? (
        products.length === 0 ? (
          <p>Cargando productos...</p>
        ) : (
          <p>No hay productos que coincidan con el filtro.</p>
        )
      ) : (
        <div style={{ display: "flex", gap: "20px", flexWrap: "wrap" }}>
          {displayed.map((p) => (
            <ProductCard key={p.id} product={p} />
          ))}
        </div>
      )}
    </div>
  );
}

export default Home;
