import React from "react";

const ProductCard = ({ product }) => {
  return (
    <div style={styles.card}>
      <img src={product.image} alt={product.name} style={styles.img} />
      <div style={{ padding: "16px" }}>
        <h3 style={{ margin: "0 0 8px 0", fontSize: "16px", fontWeight: "600", color: "#ffffff" }}>{product.name}</h3>
        <p style={{ margin: "0 0 16px 0", fontSize: "18px", fontWeight: "700", color: "#ffffff" }}>
          ${product.price.toLocaleString()}
        </p>
        <button style={styles.button}>Comprar</button>
      </div>
    </div>
  );
};

const styles = {
  card: {
    padding: "0",
    border: "1px solid #333333",
    borderRadius: "8px",
    textAlign: "left",
    backgroundColor: "#1a1a1a",
    boxShadow: "0 4px 12px rgba(255, 255, 255, 0.08)",
    overflow: "hidden",
    transition: "transform 0.3s ease, box-shadow 0.3s ease",
    cursor: "pointer",
  },
  img: {
    width: "100%",
    height: "250px",
    objectFit: "cover",
    borderRadius: "0",
  },
  button: {
    marginTop: "0",
    padding: "14px 16px",
    width: "100%",
    backgroundColor: "#ffffff",
    color: "#000000",
    borderRadius: "0",
    border: "none",
    cursor: "pointer",
    fontSize: "14px",
    fontWeight: "600",
    textTransform: "uppercase",
    letterSpacing: "0.5px",
    transition: "background-color 0.3s ease",
  },
};

export default ProductCard;
